const Header = (props) =>{

  return(
      <>
         <h1>{props.course}</h1> 
      </>
  )

}

const Part = (props) =>  (
  <p>
    {props.name} {props.exercises}
  </p>
)

const Content = (props) => {
  return (
    <div>
      {props.parts.map(part => {
        <Part part={part}/>
      })}
    </div>
  )
}

const Total = (props) => {
  return (
    <p>
      Number of exercises {props.exercises}
    </p>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }

  const parts = [part1, part2, part3]

  return (
    <div>
      <Header course={course}/>
      <Content parts={parts} />
      <Total exercises={parts.map(part=>part.exercises).reduce((acc, val)=> acc+val, 0)} />
    </div>
  )
}

export default App
